import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import ShareButton from './ShareButton';
import FavoriteButton from './FavoriteButton';

class DrinkDetailsCard extends Component {
  render() {
    const indexNine = 9;
    const indexTwentyNine = 29;
    const indexFortyNine = 49;
    const { drinkDetails } = this.props;
    const ingredients = Object.values(drinkDetails)
      .slice(indexNine, indexTwentyNine);
    const measurements = Object.values(drinkDetails)
      .slice(indexTwentyNine, indexFortyNine);

    return (
      <div>
        <img
          data-testid="recipe-photo"
          alt="Foto do prato"
          src={ drinkDetails.strDrinkThumb }
          width="300px"
          height="250px"
        />
        <h1 data-testid="recipe-title">{ drinkDetails.strDrink }</h1>
        <ShareButton id={ drinkDetails.idDrink } />
        <FavoriteButton id={ drinkDetails.idDrink } />
        <p data-testid="recipe-category">{ drinkDetails.strCategory }</p>
        <ul>
          { ingredients
            .map((item, index) => (item
              ? (
                <li
                  data-testid={ `${index}-ingredient-name-and-measure` }
                  key={ index }
                >
                  {`${item} - ${measurements[index]}`}
                </li>)
              : ''))}
        </ul>
        <p data-testid="instructions">{ drinkDetails.strInstructions }</p>
        <iframe
          data-testid="video"
          width="300px"
          height="250px"
          title="VÃ­deo da Receita"
          src={ drinkDetails.strYoutube }
        />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  drinkDetails: state.drinkReducer.drinkDetails,
});

DrinkDetailsCard.propTypes = {
  drinkDetails: PropTypes.shape({
    strDrinkThumb: PropTypes.string,
    strDrink: PropTypes.string,
    strCategory: PropTypes.string,
    strInstructions: PropTypes.string,
    strYoutube: PropTypes.string,
    idDrink: PropTypes.string,
  }).isRequired,
};

export default connect(mapStateToProps)(DrinkDetailsCard);
