import React, { Component } from 'react'
import { connect } from 'react-redux'
import { fetchFoodListByCategory } from '../redux/actions';

class Categories extends Component {
  render() {
    const { foodCategories, actionFetchFoodByCategory } = this.props;
    return (
      <div>
        <button
        type="button"
        name="category"
        >
          All
        </button>
        { foodCategories.map((item) => {
          return(
          <button 
            type="button"
            name="category"
            key={ item.strCategory }
            data-testid={`${item.strCategory}-category-filter`}
            onClick={ () => actionFetchFoodByCategory(item.strCategory) }
          >
            { item.strCategory }
          </button>
          );
        }) }

      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  foodCategories: state.foodReducers.foodCategoriesList,
});

const mapDispatchToProps = (dispatch) => ({
actionFetchFoodByCategory: (category) => dispatch(fetchFoodListByCategory(category)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Categories);
