import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchDrinkID } from '../redux/actions/drinkActions';

class DrinkDetails extends Component {
  componentDidMount() {
    const { match: { params: { id } }, fetchDrinkByID, fetchDrink } = this.props;
    fetchDrinkByID(id);
    fetchDrink('');
  }


  render() {
    return (
      <div>
        detalhes da bebida
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  fetchDrinkByID: (id) => dispatch(fetchDrinkID(id)),
})

export default connect(null, mapDispatchToProps)(DrinkDetails);
