import React, { Component } from 'react';
import { connect } from 'react-redux';
import { executeSearch, updateQuery, updateSelectedFilter } from '../redux/actions/searchBarActions';

class SearchBar extends Component {

  handleSearchClick() { 
    const { query, selectedFilter, executeSearchAction } = this.props;
  }
  render() {
    const { queryAction, selectedFilterAction, query, selectedFilter, executeSearchAction } = this.props;
    return (
      <div>
        <input
          type="text"
          data-testid="search-input"
          placeholder="FaÃ§a sua pequisa"
          value={ query }
          onChange={({target})=> queryAction(target.value) }
        />
        <label htmlFor="search-ingredient">
          Ingrediente:
          <input
            name="filter"
            id="search-ingredient"
            value="ingredient"
            type="radio"
            data-testid="ingredient-search-radio"
            onChange={()=> selectedFilterAction("ingredient")}
          />
        </label>
        <label htmlFor="search-nome">
          Nome:
          <input
            name="filter"
            id="search-nome"
            value="name"
            type="radio"
            data-testid="name-search-radio"
            onChange={()=> selectedFilterAction("name")}
          />
        </label>
        <label htmlFor="search-first-letter">
          Primeira Letra:
          <input
            name="filter"
            id="search-first-letter"
            value="firstLetter"
            type="radio"
            data-testid="first-letter-search-radio"
            onChange={()=> selectedFilterAction("firstLetter")}
          />
        </label>
        <button
          type="button"
          data-testid="exec-search-btn"
           onClick={() => executeSearchAction(query, selectedFilter)}
        >
          Buscar
        </button>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  query: state.searchBarReducer.query,
  selectedFilter: state.searchBarReducer.selectedFilter,
});

const mapDispatchToProps = (dispatch) => ({
  queryAction: (query) => dispatch(updateQuery(query)),
  selectedFilterAction: (filter) => dispatch(updateSelectedFilter(filter)),
  executeSearchAction: (query, filter) => dispatch(executeSearch(query, filter)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SearchBar);
