export const UPDATE_QUERY = 'UPDATE_QUERY';
export const UPDATE_SELECTED_FILTER = 'UPDATE_SELECTED_FILTER';

export const updateQuery = (payload) => ({
  type: UPDATE_QUERY,
  payload,
});

export const updateSelectedFilter = (payload) => ({
  type: UPDATE_SELECTED_FILTER,
  payload,
});
