import { FOOD_LIST_SUCCESS } from '../actions';

const INITIAL_STATE = {
  foodCardsList: [],
  foodCategoriesList: [],
  selectedCategory: 'All',
};

function foodReducers(state = INITIAL_STATE, action) {
  switch (action.type) {
  case FOOD_LIST_SUCCESS:
    return {
      ...state,
      foodCardsList: action.payload.slice(0,12),
    };
  default:
    return state;
  }
}

export default foodReducers;
